import React from 'react';
import {Container, Row} from "react-bootstrap";
import {useEffect} from "react";
import {devicesFromBasket} from "../http/deviceAPI";
import {useAction} from "../hooks/useAction";
import BasketList from "../components/BasketList";
import {useSelector} from "react-redux";

const Basket = () => {
    const {isUserId, isBasket} = useSelector(state => state.userReducer)
    const {ASetBasket, ASetBasketCount, ASetEmail, ASetUserId} = useAction()

    //const token = localStorage.getItem('token')

    // useEffect(() => {
    //     if (token) {
    //         const decode = jwt_decode(token)
    //         ASetEmail(decode.email)
    //         ASetUserId(decode.id)
    //     }
    // })

    useEffect(() => {
        devicesFromBasket({id: isUserId}).then(data => {ASetBasket(data.devicesData); ASetBasketCount(data.countData)}).catch(e => console.log('errorA'))
    }, [])

    return (
        <Container>
            <Row className="mt-2">
                <BasketList />
            </Row>
        </Container>
    );
};

export default Basket;
