import React, {useEffect} from 'react';
import {Col, Container, Row} from "react-bootstrap";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import {useAction} from "../hooks/useAction";
import {devicesFromBasket, fetchAllRating, fetchBrand, fetchDevice, fetchTypes} from "../http/deviceAPI";
import Pages from "../components/Pages";
import {useSelector} from "react-redux";

const Shop = () => {
    const {ASetTypes, ASetBrands, ASetDevices, ASetTotalCount, ASetBasket, ASetBasketCount, ASetRating} = useAction()
    const {isPage, isLimit, isSelectedType, isSelectedBrand, isBasket, isDevices} = useSelector(state => state.deviceReducer)
    const {isUserId} = useSelector(state => state.userReducer)

    useEffect(() => {
        fetchTypes().then(data => ASetTypes(data))
        fetchBrand().then(data => ASetBrands(data))
        fetchDevice(null, null, isPage, isLimit).then(data => {
            ASetDevices(data.rows)
            ASetTotalCount(data.count)
        })
            .catch(e => console.log('errorA'))
    }, [])

    useEffect(() => {
        fetchAllRating()
            .then(data => ASetRating(data))
    }, [])

    useEffect(() => {
        fetchDevice(isSelectedType, isSelectedBrand, isPage, isLimit).then(data => {
            //console.log(data)
            ASetDevices(data.rows)
            ASetTotalCount(data.count)
        })
    }, [isSelectedType, isSelectedBrand, isPage])

        //console.log(isDevices)

    return (
        <Container>
            <Row className="mt-2">
                <Col md={2}>
                    <TypeBar />
                </Col>
                <Col md={10}>
                    <BrandBar />
                    <DeviceList />
                    <Pages />
                </Col>
            </Row>
        </Container>
    );
};

export default Shop;
