import React, {useState} from 'react';
import {Button, Card, Col, Image} from "react-bootstrap";
import star from '../assets/Star.png'
import basket from '../assets/basket.png'
import {useNavigate} from "react-router-dom";
import {DEVICE_ROUTE} from "../utils/consts";
import {useAction} from "../hooks/useAction";
import {useSelector} from "react-redux";

const DeviceItem = (device) => {
    device = device.device
    const navigate = useNavigate()
    const [productCount, setProductCount] = useState(0)
    const {isBasketCount, isBrands} = useSelector(state => state.deviceReducer)

    //const devicesCount = isBasketCount.filter(data => data.deviceId === device.id).then(data => setDd(data)).catch(e => console.log('errrr'))
        const brandData = isBrands.filter(data => data.id === device.brandId)
    //console.log(isBasketCount)


    //console.log(isBasketCount)
    //console.log(devicesCount[0].quantity)
    //setNam(devicesCount[0].quantity)
    //console.log(nam)




    // const nameGoods = () => {if (devicesCount[0].quantity) {
    //     return 'devicesCount[0].quantity'
    // }else {
    //     return '0'
    // }}

    //console.log(devicesCount[0].quantity)

    const product = () => {
        setProductCount(() => productCount++)
    }

    return (
        <Col md={3} className="mt-3" >
            <Card style={{width: 150, cursor: "pointer"}} border={"light"} onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}>
                <div style={{height: 150, display: 'flex', justifyContent: 'center'}}>
                    <Image style={{maxWidth: 150, maxHeight:150}}  src={process.env.REACT_APP_API_URL + device.img}/>
                </div>
                <div className="mt-1 d-flex justify-content-between align-items-center">
                    <div>{brandData[0].name}</div>
                    <div className="d-flex align-items-center">
                        <div>{device.rating}</div>
                        <Image style={{width: 15, height: 15}} src={star}/>
                    </div>
                </div>
                <div
                    style={{display: "flex", justifyContent: "space-between"}}
                >
                    <div className="text-black-50">{device.name}</div>
                    {/*<div style={{color: "blue"}}>x{devicesCount[0].quantity}</div>*/}
                </div>
            </Card>
            <Button
                style={{width: 150, height: 29, padding: 0}}
                variant={"outline-secondary"}
                onClick={() => product()}
                //value={productCount}
            >
                <Image style={{width: 22, height: 22}} src={basket}/> {productCount}
            </Button>
        </Col>
    );
};

export default DeviceItem;
