import React, {useEffect} from 'react';
import {Col, Container, Row} from "react-bootstrap";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import {useAction} from "../hooks/useAction";
import {devicesFromBasket, fetchBrand, fetchDevice, fetchTypes} from "../http/deviceAPI";
import Pages from "../components/Pages";
import {useSelector} from "react-redux";

const Shop = () => {
    const {ASetTypes, ASetBrands, ASetDevices, ASetTotalCount, ASetBasket, ASetBasketCount} = useAction()
    const {isPage, isUserId, isLimit, isSelectedType, isSelectedBrand, isBrands} = useSelector(state => state.deviceReducer)

    useEffect(() => {
        fetchTypes().then(data => ASetTypes(data))
        fetchBrand().then(data => ASetBrands(data))
        fetchDevice(null, null, isPage, isLimit).then(data => {
            ASetDevices(data.rows)
            ASetTotalCount(data.count)
        })
        devicesFromBasket({id: isUserId})
            .then(data => {
                ASetBasket(data.devicesData);
                ASetBasketCount(data.countData)
            })
            .catch(e => console.log('errorA'))
    }, [])

    useEffect(() => {
        fetchDevice(isSelectedType, isSelectedBrand, isPage, isLimit).then(data => {
            ASetDevices(data.rows)
            ASetTotalCount(data.count)
        })
    }, [isSelectedType, isSelectedBrand, isPage])

    return (
        <Container>
            <Row className="mt-2">
                <Col md={3}>
                    <TypeBar />
                </Col>
                <Col md={9}>
                    <BrandBar />
                    <DeviceList />
                    <Pages />
                </Col>
            </Row>
        </Container>
    );
};

export default Shop;
