import {deviceActionEnum} from "./types";

const initialState = {
    isTypes: [
        {id: 1, name: 'Холодильник'},
        {id: 2, name: 'Смартфони'}
    ],
    isBrands: [
        {id: 1, name: 'Samsung'},
        {id: 2, name: 'Apple'}
    ],
    isDevices: [
        {id: 1, name: 'iPhone 12 pro', price: 1100, rating: 5, img: 'https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png'},
        {id: 2, name: 'iPhone 12 pro', price: 1100, rating: 5, img: 'https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png'},
        {id: 3, name: 'iPhone 12 pro', price: 1100, rating: 5, img: 'https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png'},
        {id: 4, name: 'iPhone 12 pro', price: 1100, rating: 5, img: 'https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png'},
        {id: 5, name: 'iPhone 12 pro', price: 1100, rating: 5, img: 'https://www.purposechurch.com/wp-content/uploads/2017/10/fpo400x300.png'},
    ]
}

export const deviceReducer = (state = initialState, action) => {
    switch (action.type){
        case deviceActionEnum.SET_TYPES:
            return {...state, isTypes: action.payload};
        case deviceActionEnum.SET_BRANDS:
            return {...state, isBrands: action.payload};
        case deviceActionEnum.SET_DEVICES:
            return {...state, isDevice: action.payload};
        default:
            return state;
    }
}
