import './App.css';
import {RouterProvider} from "react-router";
import {useAppRouter} from "./routes/AppRouter";
import {useEffect, useState} from "react";
import {check} from "./http/userAPI";
import {Spinner} from "react-bootstrap";
import {useAction} from "./hooks/useAction";
import jwt_decode from "jwt-decode";
import {devicesFromBasket} from "./http/deviceAPI";
import {useSelector} from "react-redux";

function App() {
  const {ASetUser, ASetAuth, ASetEmail, ASetUserId, ASetBasket, ASetBasketCount} = useAction()
  const {isUserId} = useSelector(state => state.deviceReducer)
  const [loading, setLoading] = useState(true)

  const token = localStorage.getItem('token')



  useEffect(() => {
    setTimeout(() => {
      check().then(data => {
        ASetUser(true)
        ASetAuth(true)
        if (token) {
          const decode = jwt_decode(token)
          ASetEmail(decode.email)
          ASetUserId(decode.id)
        }
      }).finally(() => setLoading(false))
    }, 500)

  }, [])

  const router = useAppRouter()

  if (loading) {
    return <Spinner animation={"grow"}/>
  }

  return (
        <RouterProvider router={router}/>
  );
}

export default App;
