import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import bigStar from '../assets/bigStar.png'
import {useParams} from "react-router";
import {countDeviceFromBasket, createBasketDevice, fetchOneDevice, fetchOneGlobalRating} from "../http/deviceAPI";
import {useSelector} from "react-redux";
import {useAction} from "../hooks/useAction";

const DevicePage = () => {
    const [device, setDevice] = useState({info: []})
    const [middleRating, setMiddleRating] = useState()
    const [personalRating, setPersonalRating] = useState()
    const {isUserId} = useSelector(state => state.userReducer)
    const {isBasketCount, isRating, isRatingCount} = useSelector(state => state.deviceReducer)
    const {ASetBasketCount, ASetRating, ASetRatingCount} = useAction()
    const {id} = useParams()

    const addInBasket = () => {
        createBasketDevice({id: isUserId, deviceId: device.id})
            .then(() => countDeviceFromBasket({userId: isUserId, deviceId: device.id}))
            .then(data => {ASetBasketCount(data[0].quantity)})

            .catch(() => console.log('error'))
    }
    //console.log(isBasketCount)

    useEffect(() => {
        fetchOneDevice(id).then(data => setDevice(data))
    }, [])

    useEffect(() => {
        if (isUserId) {
            countDeviceFromBasket({userId: isUserId, deviceId: id})
                .then(data => { if (![]) {ASetBasketCount(data[0].quantity)} })
                .catch(() => console.log('error'))
        }
    }, [isBasketCount])

    useEffect(() => {
        fetchOneGlobalRating({deviceId: id})
            .then(data => {
                ASetRating(data.ratingOneGlobal)
                ASetRatingCount(data.ratingCount)

            })
    }, [])
    //console.log(device)
    useEffect(() => {
        try {
            try {
                const ratingGoodsCurrentFiltered = isRating.filter(data => data.deviceId === device.id)
                const ratingCountFiltered = isRatingCount.filter(data => data.deviceId === device.id)
                let sumRate = 0
                let totalRate

                for (let i = 0; i < ratingGoodsCurrentFiltered.length; i++) {
                    sumRate += ratingGoodsCurrentFiltered[i].rate;
                }
                totalRate = sumRate / ratingCountFiltered[0].quantity

                setGlobalRating(totalRate)
            } catch (e) {
            }
        } catch (e) {}
    }, [])




    return (
        <Container className="mt-3">
            <Row>
                <Col md={4}>
                    <div style={{height: 300, display: 'flex', justifyContent: 'center'}}>
                        <Image style={{maxWidth: 300, maxHeight:300}}  src={process.env.REACT_APP_API_URL + device.img}/>
                    </div>
                </Col>
                <Col md={4}>
                    <div className="d-flex flex-column align-items-center">
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{background: `url(${bigStar}) no-repeat center center`, width: 240, height: 240, backgroundSize: 'cover', fontSize: 64}}
                        >
                            {device.rating}
                        </div>
                    </div>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height:300, fontSize: 32, border: '5px solid lightgray', borderRadius: 30}}
                    >
                        <h3>Від: {device.price}$</h3>
                        {isUserId && <Button
                            variant={"outline-dark"}
                            onClick={() => addInBasket()}
                        >
                            Добавити в корзину {isBasketCount > 0 ? `x${isBasketCount}` : ''}
                        </Button>}
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {device.info.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default DevicePage;

























