import React from 'react';
import {Button, Container, Nav, Navbar} from "react-bootstrap";
import {useSelector} from "react-redux";
import {NavLink} from "react-router-dom";
import {SHOP_ROUTE} from "../utils/consts";
import {useAction} from "../hooks/useAction";

const NavBar = () => {
    const {isAuth} = useSelector(state => state.userReducer)
    const {ASetAuth} = useAction()

    return (
        <Navbar bg="dark" variant="dark" >
            <Container>
                <NavLink style={{color: 'white'}} to={SHOP_ROUTE}>Купи девайс</NavLink>
                {isAuth ?
                    <Nav className="ml-auto mr-auto">
                        <Button variant={"outline-light"}>Адмін панель</Button>
                        <Button variant={"outline-light"} className="ml-4">Вийти</Button>
                    </Nav>
                    :
                    <Nav className="ml-auto mr-auto">
                        <Button variant={"outline-light"} onClick={() => ASetAuth(true)}>Авторизація</Button>
                    </Nav>
                }
            </Container>
        </Navbar>
    );
};

export default NavBar;
