import React from 'react';
import {Button, Nav, Navbar} from "react-bootstrap";
import {useSelector} from "react-redux";
import {NavLink} from "react-router-dom";
import {SHOP_ROUTE} from "../utils/consts";
import {useAction} from "../hooks/useAction";

const NavBar = () => {
    const {isAuth} = useSelector(state => state.userReducer)
    const {ASAuth} = useAction()

    return (
        <Navbar bg="dark" variant="dark" style={{display: 'flex', justifyContent: "space-between", padding: "8px 15px"}}>
                <NavLink style={{color: 'white'}} to={SHOP_ROUTE}>Купи девайс</NavLink>
            {isAuth ?
                <Nav className="ml-auto mr-auto" style={{maxWidth: "100% !important"}}>
                    <Button variant={"outline-light"}>Адмін панель</Button>
                    <Button variant={"outline-light"} style={{marginLeft: "5px"}}>Авторизація</Button>
                </Nav>
                :
                <Nav className="ml-auto mr-auto" style={{maxWidth: "100% !important"}}>
                    <Button variant={"outline-light"} onClick={() => ASAuth(true)}>Авторизація</Button>
                </Nav>
            }


        </Navbar>
    );
};

export default NavBar;
