import React from 'react';
import {Button, Card, Col, Image} from "react-bootstrap";
import star from '../assets/Star.png'
import {useNavigate} from "react-router-dom";
import {DEVICE_ROUTE} from "../utils/consts";

const DeviceItem = (device) => {
    device = device.device
    const navigate = useNavigate()

    return (
        <Col md={3} className="mt-3" >
            <Card style={{width: 150, cursor: "pointer"}} border={"light"} onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}>
                <div style={{height: 150, display: 'flex', justifyContent: 'center'}}>
                    <Image style={{maxWidth: 150, maxHeight:150}}  src={process.env.REACT_APP_API_URL + device.img}/>
                </div>
                <div className="text-black-50 mt-1 d-flex justify-content-between align-items-center">
                    <div>Samsung...</div>
                    <div className="d-flex align-items-center">
                        <div>{device.rating}</div>
                        <Image style={{width: 15, height: 15}} src={star}/>
                    </div>
                </div>
                <div>{device.name}</div>
            </Card>
            <Button
                style={{width: 150}}
                variant={"danger"}
                onClick={() => {}}
            >
                Добавити в корзину
            </Button>
        </Col>
    );
};

export default DeviceItem;
