import {makeAutoObservable} from "mobx";
import {createContext} from "react";

class UserStore {
  constructor() {
    this._isAuth = false
    this._user = {}
    makeAutoObservable(this)
  }

  setIsAuth(bool) {
    this._isAuth = bool
  }
  setUser(user) {
    this._user = user
  }

  get isAuth() {
    return this._isAuth
  }
  get user() {
    return this._user
  }
}

export const Context = createContext(null)

function App() {
  return (
      //<Provider store={store}>
      <Context.Provider value={{
        user: new UserStore()
      }}>
        <RouterProvider router={router}/>
      </Context.Provider>
      //</Provider>
  );
}

export default App;
