import React from 'react';
import Modal from "react-bootstrap/Modal";
import {Form, Button, Dropdown} from "react-bootstrap";
import {useSelector} from "react-redux";

const CreateType = ({show, onHide}) => {
    const {isTypes, isBrands} = useSelector(state => state.deviceReducer)

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить пристрій
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown>
                        <Dropdown.Toggle>Виберіть тип</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {isTypes.map((type) =>
                                <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Dropdown className="mt-1">
                        <Dropdown.Toggle>Виберіть бренд</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {isBrands.map((brand) =>
                                <Dropdown.Item key={brand.id}>{brand.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрити</Button>
                <Button variant="outline-success" onClick={onHide}>Добавити</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateType;
