const initialState = {
    isAuth: false,
    isUser: {}
}

const userActionEnum = {
    SET_AUTH: 'SET_AUTH',
    SET_USER: 'SET_USER'
}

const eventReducer = (state = initialState, action) => {
    switch (action.type){
        case userActionEnum.SET_AUTH:
            return {...state, isAuth: action.payload};
        case userActionEnum.SET_USER:
            return {...state, isUser: action.payload};
    }
}
