import './App.css';
import {RouterProvider} from "react-router";
import {useAppRouter} from "./routes/AppRouter";
import {useDispatch} from "react-redux";
import {useEffect, useState} from "react";
import {check} from "./http/userAPI";
import {userActionCreators} from "./store/action-creators/user";
import {Spinner} from "react-bootstrap";

function App() {

  const dispatch = useDispatch()
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    setTimeout(() => {
      check().then(data => {
        dispatch(userActionCreators.ASetUser(true))
        dispatch(userActionCreators.ASetAuth(true))
      }).finally(() => setLoading(false))
    }, 1000)
  }, [])



  return (
      <RouterProvider router={useAppRouter()}/>
  );
  if (loading) {
    return <Spinner animation={"grow"}/>
  }
}

export default App;
