import React, {useState} from 'react';
import Modal from "react-bootstrap/Modal";
import {Form, Button, Dropdown, Row, Col} from "react-bootstrap";
import {useSelector} from "react-redux";

const CreateType = ({show, onHide}) => {
    const {isTypes, isBrands} = useSelector(state => state.deviceReducer)
    const [info, setInfo] = useState([])

    const addInfo = () => {
        setInfo([...info, {title: '', description: '', number: Date.now()}])
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить пристрій
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown>
                        <Dropdown.Toggle>Виберіть тип</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {isTypes.map((type) =>
                                <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Dropdown className="mt-2">
                        <Dropdown.Toggle>Виберіть бренд</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {isBrands.map((brand) =>
                                <Dropdown.Item key={brand.id}>{brand.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control className="mt-3" placeholder="Введіть назву пристрою"/>
                    <Form.Control className="mt-3" placeholder="Введіть вартість пристрою" type="number"/>
                    <Form.Control className="mt-3" type="file"/>
                    <hr/>
                    <Button
                        variant={"outline-dark"}
                        onClick={addInfo}
                    >
                        Добавити новий пристрій
                    </Button>
                    {info.map(i =>
                        <Row>
                            <Col md={4}>
                                <Form.Control placeholder="Введіть назву властивості"/>
                            </Col>
                            <Col md={4}>
                                <Form.Control placeholder="Введіть опис властивості"/>
                            </Col>
                            <Col md={4}>

                            </Col>
                        </Row>
                    )}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрити</Button>
                <Button variant="outline-success" onClick={onHide}>Добавити</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateType;
