import React from 'react';
import {Container, Row} from "react-bootstrap";
import DeviceList from "../components/DeviceList";
import {useEffect} from "react";
import {fetchAllDevice, fetchBasket, fetchBasketDevices} from "../http/deviceAPI";
import {useAction} from "../hooks/useAction";
import BasketList from "../components/BasketList";
import {useSelector} from "react-redux";

const Basket = () => {
    const {isUserId} = useSelector(state => state.userReducer)
    const {ASetBasket} = useAction()

    useEffect(() => {
        fetchAllDevice().then(data => {ASetBasket(data)}).catch(e => console.log('errorA'))
    }, [])

    useEffect(() => {
        fetchBasket({userId: isUserId})
            .then(basketId => fetchBasketDevices())
    })

    return (
        <Container>
            <Row className="mt-2">
                <BasketList />
            </Row>
        </Container>
    );
};

export default Basket;
